xme_core_dataManager_dataPacketId_t
getDataPacketID
(
    xme_core_component_t componentID,
    uint16_t portIndex
)
{
    xme_status_t status;
    xme_core_nodeMgr_compRep_componentHandle_t componentHandle;
    xme_core_nodeMgr_compRep_portHandle_t portHandle;

    status = xme_core_nodeMgr_compRep_getComponentInstance(XME_CORE_NODE_LOCAL_NODE_ID, componentID, &componentHandle);
    XME_ASSERT_RVAL(XME_STATUS_SUCCESS == status, XME_CORE_DATAMANAGER_DATAPACKETID_INVALID);

    portHandle = xme_core_nodeMgr_compRep_getPort(componentHandle, portIndex);

    return xme_core_nodeMgr_compRep_getDataPacketID(portHandle);
}

xme_core_exec_functionDescriptor_t*
getFunctionDescriptor
(
    xme_core_component_t componentID,
    uint16_t functionIndex
)
{
    xme_status_t status;
    xme_core_nodeMgr_compRep_componentHandle_t componentHandle;
    xme_core_nodeMgr_compRep_functionHandle_t functionHandle;

    status = xme_core_nodeMgr_compRep_getComponentInstance(XME_CORE_NODE_LOCAL_NODE_ID, componentID, &componentHandle);
    XME_ASSERT_RVAL(XME_STATUS_SUCCESS == status, NULL);

    functionHandle = xme_core_nodeMgr_compRep_getFunction(componentHandle, functionIndex);

    return (xme_core_exec_functionDescriptor_t*)xme_core_nodeMgr_compRep_getFunctionDescriptor(functionHandle);
}

xme_status_t
createComponentInstances(void)
{
    xme_status_t status;

    // Create statically configured component instance 'client1' of type 'client'
    {
        xme_core_nodeMgr_compRep_componentBuilder_t* builder = NULL;
        xme_core_nodeMgr_compRep_componentHandle_t componentHandle;

        builder = xme_core_nodeMgr_compRep_createBuilder(XME_CORE_NODE_LOCAL_NODE_ID, (xme_core_componentType_t)5001);
        XME_CHECK_MSG(NULL != builder, XME_STATUS_INTERNAL_ERROR, XME_LOG_FATAL, "%s:%d Creation of component instance 'client1' of type 'client' failed. Builder could not be created.\n", __FILE__, __LINE__);
        xme_core_nodeMgr_compRep_builderSetComponentID(builder, (xme_core_component_t)128);
        xme_core_nodeMgr_compRep_builderSetExecutionPeriod(builder, 0, xme_hal_time_timeIntervalFromSeconds(5ull)); // Function 'sendRequest'
        status = xme_core_nodeMgr_compRep_build(builder, &componentHandle);
        XME_CHECK_MSG(XME_STATUS_SUCCESS == status, XME_STATUS_INTERNAL_ERROR, XME_LOG_FATAL, "%s:%d Creation of component instance 'client1' of type 'client' failed. Building of component failed.\n", __FILE__, __LINE__);

        status = xme_core_nodeMgr_compRep_setStateToAnnounced(componentHandle);
        XME_CHECK_MSG(XME_STATUS_SUCCESS == status, XME_STATUS_INTERNAL_ERROR, XME_LOG_FATAL, "%s:%d Creation of component instance 'client1' of type 'client' failed. Setting state to ANNOUNCED failed.\n", __FILE__, __LINE__);

        status = xme_core_nodeMgr_compRep_createAndRegisterComponent(componentHandle);
        XME_CHECK_MSG(XME_STATUS_SUCCESS == status, XME_STATUS_INTERNAL_ERROR, XME_LOG_FATAL, "%s:%d Creation of component instance 'client1' of type 'client' failed. Registration and port allocation failed.\n", __FILE__, __LINE__);

        ports[0] = getDataPacketID((xme_core_component_t)128, 0);
        ports[1] = getDataPacketID((xme_core_component_t)128, 1);
        
        client1FuncDescs[0] = getFunctionDescriptor((xme_core_component_t)128, 0);
        client1FuncDescs[1] = getFunctionDescriptor((xme_core_component_t)128, 1);
    }

    return XME_STATUS_SUCCESS;
}
