xme_status_t
addDataPacketTransferEntries(void)
{
    xme_status_t status;

    status = xme_core_broker_addDataPacketTransferEntry(ports[1], ports[6]); // client.clientRequest -> channelInjector.configuration[0].in (client.clientRequest)
    XME_CHECK_MSG(XME_STATUS_SUCCESS == status, XME_STATUS_INTERNAL_ERROR, XME_LOG_FATAL, "%s:%d Call to xme_core_broker_addDataPacketTransferEntry() failed for source data packet ID %d and destination data packet ID %d with status %d.\n", __FILE__, __LINE__, ports[1], ports[6], status);
    status = xme_core_broker_addDataPacketTransferEntry(ports[2], ports[0]); // demarshaler.configuration[0].out (client.clientResponse) -> client.clientResponse
    XME_CHECK_MSG(XME_STATUS_SUCCESS == status, XME_STATUS_INTERNAL_ERROR, XME_LOG_FATAL, "%s:%d Call to xme_core_broker_addDataPacketTransferEntry() failed for source data packet ID %d and destination data packet ID %d with status %d.\n", __FILE__, __LINE__, ports[2], ports[0], status);
    status = xme_core_broker_addDataPacketTransferEntry(ports[4], ports[3]); // udpReceive.configuration[0].out (client.clientResponse) -> demarshaler.configuration[0].in (client.clientResponse)
    XME_CHECK_MSG(XME_STATUS_SUCCESS == status, XME_STATUS_INTERNAL_ERROR, XME_LOG_FATAL, "%s:%d Call to xme_core_broker_addDataPacketTransferEntry() failed for source data packet ID %d and destination data packet ID %d with status %d.\n", __FILE__, __LINE__, ports[4], ports[3], status);
    status = xme_core_broker_addDataPacketTransferEntry(ports[5], ports[8]); // channelInjector.configuration[0].out (client.clientRequest) -> marshaler.configuration[0].in (client.clientRequest)
    XME_CHECK_MSG(XME_STATUS_SUCCESS == status, XME_STATUS_INTERNAL_ERROR, XME_LOG_FATAL, "%s:%d Call to xme_core_broker_addDataPacketTransferEntry() failed for source data packet ID %d and destination data packet ID %d with status %d.\n", __FILE__, __LINE__, ports[5], ports[8], status);
    status = xme_core_broker_addDataPacketTransferEntry(ports[7], ports[9]); // marshaler.configuration[0].out (client.clientRequest) -> udpSend.configuration[0].in (client.clientRequest)
    XME_CHECK_MSG(XME_STATUS_SUCCESS == status, XME_STATUS_INTERNAL_ERROR, XME_LOG_FATAL, "%s:%d Call to xme_core_broker_addDataPacketTransferEntry() failed for source data packet ID %d and destination data packet ID %d with status %d.\n", __FILE__, __LINE__, ports[7], ports[9], status);

    return XME_STATUS_SUCCESS;
}

int
main(int argc, char* argv[])
{
    xme_status_t status;

    status = xmeInit(argc, argv);
    XME_CHECK_MSG
    (
        XME_STATUS_SUCCESS == status,
        status,
        XME_LOG_FATAL,
        "Error occurred during initialization of CHROMOSOME core components. Aborting execution.\n"
    );

    xme_hal_env_setConsoleTitle("client1Node");

    createComponentTypeManifests();

    status = registerTopicsAndAttributes();
    XME_CHECK_MSG
    (
        XME_STATUS_SUCCESS == status,
        status,
        XME_LOG_FATAL,
        "Registration of topics and attributes failed. Aborting execution.\n"
    );

    // PROTECTED REGION ID(CLIENT1NODE_MAIN_BEFORE_COMPONENT_CREATION) ENABLED START

    // PROTECTED REGION END

    status = createComponentInstances();
    XME_CHECK(XME_STATUS_SUCCESS == status, status);

    createWaypoints();

    status = addDataPacketTransferEntries();
    XME_CHECK(XME_STATUS_SUCCESS == status, status);

    configureSchedule();

    // PROTECTED REGION ID(CLIENT1NODE_MAIN_RUN_BEFORE) ENABLED START

    // PROTECTED REGION END

    // Run the schedule
    xme_core_exec_run(0, false);

    // PROTECTED REGION ID(CLIENT1NODE_MAIN_RUN_AFTER) ENABLED START
    
    // PROTECTED REGION END

    xme_core_exec_fini();

    // Destroy waypoints
    if (NULL != demarshalerWaypointDescriptor)
    {
        destroyDemarshalerWaypointInstance(demarshalerWaypointDescriptor);
    }
    if (NULL != udpReceiveWaypointDescriptor)
    {
        destroyUdpReceiveWaypointInstance(udpReceiveWaypointDescriptor);
    }
    if (NULL != channelInjectorWaypointDescriptor)
    {
        destroyChannelInjectorWaypointInstance(channelInjectorWaypointDescriptor);
    }
    if (NULL != marshalerWaypointDescriptor)
    {
        destroyMarshalerWaypointInstance(marshalerWaypointDescriptor);
    }
    if (NULL != udpSendWaypointDescriptor)
    {
        destroyUdpSendWaypointInstance(udpSendWaypointDescriptor);
    }

    XME_LOG(XME_LOG_NOTE, "Node client1Node (nodeID %d) has shut down.\n", xme_core_node_getCurrentNodeId());

    xmeFini();

    return 0;
}
